--
-- Table structure for table `recruitment`
--

DROP TABLE IF EXISTS `recruitment`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `recruitment` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '自增ID',
  `type` int(4) NOT NULL DEFAULT '1' COMMENT '类别',
  `name` varchar(255) NOT NULL DEFAULT '' COMMENT '职位名称',
  `keyword` varchar(255) DEFAULT '' COMMENT '关键字',
  `preview` varchar(255) DEFAULT '' COMMENT '预览图',
  `summary` varchar(255) DEFAULT '' COMMENT '摘要',
  `content` mediumtext NOT NULL COMMENT '正文',
  `source` varchar(255) DEFAULT '' COMMENT '来源',
  `click` int(10) NOT NULL DEFAULT '1' COMMENT '点击次数',
  `status` int(10) NOT NULL DEFAULT '1' COMMENT '状态1显示0已删除',
  `adminid` int(10) NOT NULL DEFAULT '0' COMMENT '管理员ID',
  `created` int(10) NOT NULL DEFAULT '0' COMMENT '创建时间',
  `updated` int(10) NOT NULL DEFAULT '0' COMMENT '更新时间',
  PRIMARY KEY (`id`),
  KEY `type` (`type`,`status`),
  KEY `name` (`name`)
) ENGINE=InnoDB AUTO_INCREMENT=24 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT COMMENT='招贤纳士';
/*!40101 SET character_set_client = @saved_cs_client */;

--
-- Dumping data for table `recruitment`
--

LOCK TABLES `recruitment` WRITE;
/*!40000 ALTER TABLE `recruitment` DISABLE KEYS */;
INSERT INTO `recruitment` VALUES (1,1,'APP设计师','','','','<p><br/></p><p style=\"outline: none; color: rgb(119, 119, 119); font-family: Arial, &#39;Hiragino Sans GB&#39;, 微软雅黑, 宋体; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(250, 250, 250);\">岗位职责：</p><p style=\"outline: none; color: rgb(119, 119, 119); font-family: Arial, &#39;Hiragino Sans GB&#39;, 微软雅黑, 宋体; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(250, 250, 250);\">1、负责移动App和网页的用户界面设计,根据产品原型进行具体UI设计（包括界面风格、布局细节处理、icon绘制等）； 2、独立完成UI相关图形设计工作，能根据产品的设计思路设计对应配套的UI； 3、总结和制定产品视觉规范； 4、参与产品构思及界面优化，提出对产品界面规划的见解，把控产品最终界面实现效果。</p><p style=\"outline: none; color: rgb(119, 119, 119); font-family: Arial, &#39;Hiragino Sans GB&#39;, 微软雅黑, 宋体; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(250, 250, 250);\">任职要求： <br/></p><p style=\"outline: none; color: rgb(119, 119, 119); font-family: Arial, &#39;Hiragino Sans GB&#39;, 微软雅黑, 宋体; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(250, 250, 250);\">1、美术类、设计类专业，2年以上UI设计资历； 2、熟练使用Photoshop，Illustrator等设计软件； 3、熟悉iOS和Android交互体验和UI设计规范； 4、具备良好合作态度及团队精神，色彩感、形式感强，视野开阔，创意丰富； 5、拥有良好的绘画功底者，美术基础者优先； 6、有Android、iOS等App设计经验者优先； 面试请提供个人作品。</p><p><br/></p>','百度',1,0,1,1421898602,1422266624),(2,4,'APP产品运营','','','','<p style=\"outline: none; color: rgb(119, 119, 119); font-family: Arial, &#39;Hiragino Sans GB&#39;, 微软雅黑, 宋体; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(250, 250, 250);\">岗位描述：<br style=\"outline: none;\"/>1. 负责店铺内容的更新与专题的选题、设计、版式、策划、编辑和组织完成，及时反映爆点；<br style=\"outline: none;\"/>2. 负责公司店铺对外宣传文稿策划编写；<br style=\"outline: none;\"/>3、熟悉网络推广（搜索引擎推广与SEM），户外媒体推广方案（根据当季度推广方案确定）自己能够独立操作推广；<br style=\"outline: none;\"/>4、能够撰写项目攻略与活动攻略，根据项目内容撰写关于本项目的软文，广告文案等宣传品，完场一定的SEO，论坛，博客营销等内容；<br style=\"outline: none;\"/>5. 协助其他教学相关活动、学术讨论及配合招生宣传工作。&nbsp;<br style=\"outline: none;\"/>岗位要求：<br style=\"outline: none;\"/>1. 具有良好的团队协作能力、应变能力及协作沟通能力；2. 有软文撰写、推广经验，具备较强的新闻敏感度、创新意识和创新能力； 3. 熟练掌握OFFICE办公软件和网络基本知识，具备较强的文字功底和专题策划能力，会使用Dreamweaver，Photoshop，html代码的优先熟悉网络广告操作规范、流程、发布渠道；</p><p style=\"outline: none; color: rgb(119, 119, 119); font-family: Arial, &#39;Hiragino Sans GB&#39;, 微软雅黑, 宋体; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(250, 250, 250);\">4. 具有良好的敬业精神，有较强的创新意识，能够在较大压力下工作； 5. 营销或经济类专业。</p><p style=\"outline: none; color: rgb(119, 119, 119); font-family: Arial, &#39;Hiragino Sans GB&#39;, 微软雅黑, 宋体; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(250, 250, 250);\">岗位待遇：</p><p style=\"outline: none; color: rgb(119, 119, 119); font-family: Arial, &#39;Hiragino Sans GB&#39;, 微软雅黑, 宋体; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: 22px; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(250, 250, 250);\">1. 有竞争力的薪酬、丰厚的奖金、高额提成；<br style=\"outline: none;\"/>2. 保险+带薪年假+完善培训体系；<br style=\"outline: none;\"/>3. 广阔的职业发展平台。</p><p><br/></p>','拉沟',1,0,1,1421898649,1422267711),(3,5,'人事专员','','','','<p style=\"padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体, simsun, sans-serif; font-size: 14px; line-height: 28px; white-space: normal;\"><span style=\"color:#000000\"><strong><span style=\"padding: 0px; margin: 0px;\"><span style=\"padding: 0px; margin: 0px;\">岗位职责：</span></span></strong><span style=\"padding: 0px; margin: 0px;\"><span style=\"padding: 0px; margin: 0px;\"></span><br/>1、协助上级建立健全公司招聘、培训、工资、保险、福利、绩效考核等人力资源制度建设；<br/>2、建立、维护人事档案，办理和更新劳动合同；<br/>3、执行人力资源管理各项实务的操作流程和各类规章制度的实施，配合其他业务部门工作；<br/>4、收集相关的劳动用工等人事政策及法规；<br/>5、执行招聘工作流程，协调、办理员工招聘、入职、离职、调任、升职等手续；<br/>6、协同开展新员工入职培训，业务培训，执行培训计划，联系组织外部培训以及培训效果的跟踪、反馈；<br/>7、负责员工工资结算和年度工资总额申报，办理相应的社会保险等；<br/>8、帮助建立员工关系，协调员工与管理层的关系，组织员工的活动。<br/><span style=\"padding: 0px; margin: 0px; background-color: rgb(255, 255, 255);\"><strong>任职资格：</strong></span><br/>1、人力资源或相关专业大专以上学历；<br/>2、两年以上人力资源工作经验；<br/>3、熟悉人力资源管理各项实务的操作流程，熟悉国家各项劳动人事法规政策，并能实际操作运用<br/>4、具有良好的职业道德，踏实稳重，工作细心，责任心强，有较强的沟通、协调能力，有团队协作精神；<br/>5、熟练使用相关办公软件，具备基本的网络知识。<br/>工作时间：</span></span></p><p style=\"padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体, simsun, sans-serif; font-size: 14px; line-height: 28px; white-space: normal;\"><span style=\"padding: 0px; margin: 0px;color:#000000\">五休二，8：30---17：30</span></p>','',1,0,1,1422244168,1422932762),(4,5,'客服专员','','','','<p style=\"padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体, simsun, sans-serif; font-size: 14px; line-height: 28px; white-space: normal;\"><span style=\"padding: 0px; margin: 0px;color:#000000\">岗位职责：</span></p><p style=\"padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体, simsun, sans-serif; font-size: 14px; line-height: 28px; white-space: normal;\"><span style=\"color:#000000\">（1）完成上级交代的任务，并能独立处理和解决所负责的任务；<br/>（2）与各家承运商对接，处理各家的投诉、工单处理；<br/>（3）清理各种在途、向客户反馈各种报表及其他事项。</span></p><p style=\"padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体, simsun, sans-serif; font-size: 14px; line-height: 28px; white-space: normal;\"><span style=\"padding: 0px; margin: 0px;color:#000000\">任职资格：</span></p><p style=\"padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体, simsun, sans-serif; font-size: 14px; line-height: 28px; white-space: normal;\"><span style=\"color:#000000\">（1）年龄18—40岁，能吃苦耐劳，为人亲和；</span></p><p style=\"padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体, simsun, sans-serif; font-size: 14px; line-height: 28px; white-space: normal;\"><span style=\"color:#000000\">（2）高中及以上学历，有过同行业客服经验者优先；<br/>（3）责任感强并具备团队合作精神，同时能够承受一定的工作压力<br/>（4）能熟练使用各种办公软件，具有良好的沟通协调能力。</span></p><p style=\"padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体, simsun, sans-serif; font-size: 14px; line-height: 28px; white-space: normal;\"><span style=\"padding: 0px; margin: 0px;color:#000000\">工作时间：</span></p><p style=\"padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体, simsun, sans-serif; font-size: 14px; line-height: 28px; white-space: normal;\"><span style=\"color:#000000\">（1）基本工资面议</span></p><p style=\"padding: 0px; margin-top: 0px; margin-bottom: 0px; font-family: Arial, 宋体, simsun, san